import requestsfrom bs4 import BeautifulSoupimport json"""获取信息并写进txt"""def get_xinxi():    url = ' https://map.baidu.com/?qt=bsl&uid=1203aab4fd2227e8740269b6&c=257&t=1564015808798'    html = requests.get(url)    soup = BeautifulSoup(html.content,features ="lxml")    text = soup.find('p').text    text1 = json.loads(text)['content']    #fr = open("busInfo.txt", "w+")    for i in range(len(text1)):        text = text1[i]        acc_flag = text['acc_flag']     #不知道什么归        addr = text['addr']              #总路线        area = text['area']              #地区编号        area_name = text['area_name']    #地区名        biz_type = text['biz_type']      #?        blinfo = text['blinfo']        print(acc_flag, addr, area, area_name, biz_type, end="\n")        for data in blinfo:            addr_1 = data['addr']            icon_id = data['icon_id']            name = data['name']            next_station = data['next_station']            pair_uid = data['pair_uid']            son_uid = data['son_uid']            uid = data['uid']            print(addr_1, icon_id, name, next_station, pair_uid, son_uid, uid)            input()       # fr = open("busInfo.txt", "w")        #fr.write(str(text))        #fr.write("\n\n")        #fr.close()    #print(soup.find('p').text)    #printif __name__ == "__main__":    get_xinxi()