import pymysql.cursors  # 请求import pymysqlimport jsonclass MySQL(object):    def __init__(self, user='root', passwords ='hzt123'):  # 连接数据库        self.config = {            'host': "localhost",            'user': user,            'password': passwords,            'db': 'trarecsys',            'charset': 'utf8mb4',            'cursorclass': pymysql.cursors.DictCursor        }        try:            self.db = pymysql.connect(**self.config)            self.cursor = self.db.cursor()        except:            print("connect mysql error.")    def get_search_lines(self):        sql = "SELECT line FROM bus_line"        self.cursor.execute(sql)        result = self.cursor.fetchall()        line = []        for i in range(len(result)):            #input(list(result[i].values())[0].split('(',1)[0])            line.append(list(result[i].values())[0].split('(',1)[0])        #print(line)        return line