"""查询语句：1、根据id 查询 lon,lat2、根据 station_name 查询 id表格：CREATE TABLE stations  (  id ：int(100) NULL DEFAULT NULL,  station_name ：varchar(300) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL,  lon ：double NULL DEFAULT NULL,  lat ： double NULL DEFAULT NULL,  stype ：int(11) NULL DEFAULT NULL,  line_id ：int(11) NULL DEFAULT NULL)"""import pymysql.cursors  # 请求import pymysqlclass MySQL(object):    def __init__(self, name, id, user='root', passwords ='hzt123'):  # 连接数据库        self.config = {            'host': "localhost",            'user': user,            'password': passwords,            'db': 'trarecsys',            'charset': 'utf8mb4',            'cursorclass': pymysql.cursors.DictCursor        }        self.name = name        self.a = str(id)        try:            self.db = pymysql.connect(**self.config)            self.cursor = self.db.cursor()        except:            print("connect mysql error.")    def get_lon_lat(self):        sql = "SELECT lon,lat FROM stations WHERE id = %d" % int(self.a)        self.cursor.execute(sql)        print(self.cursor.fetchone())        print(self.cursor.fetchall())    def get_id(self):        sql = "SELECT id FROM stations WHERE station_name = '%s'" % ( self.name )        self.cursor.execute(sql)        print(self.cursor.fetchone())        print(self.cursor.fetchall())